import React from 'react';
import data from '../data/data'

function SearchForm(props) {

    let searchData = data

    const handleSubmit = event => {
        event.preventDefault()
        return (
            <div className='houses-searched'>
                {searchData = data.filter(curr => {
                    return curr.locationCity.toLowerCase() === props.state.location.lower()
                })}
            </div>
        )
    }

        const handleChange = event => {
            props.setState({...props.state, form: {
                location: event.target.value
            }})
    }

  return (
    <div className="App">
      <form className='form' onSubmit={handleSubmit}>
        <h1>Show me Houses in:</h1>
        <input onChange={handleChange} name='location' type='text' value={props.state.form.location} placeholder='Location' />
        <input type='submit' id='submit'/>
      </form>
      <div className='searched-items'>
        {searchData.map((curr) => {
            return(
                <p>
                {curr.title}
                </p>
            )
        })}
      </div>
    </div>
  );
}

export default SearchForm;
